import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Precios Combustibles';
}
<div class="app-container">
  <header class="header">
    <div class="container">
      <h1>⛽ Precios de Combustibles</h1>
      <p class="subtitle">Datos actualizados del MITECO</p>
    </div>
  </header>

  <main class="container">
    <app-lista-gasolineras></app-lista-gasolineras>
  </main>
</div>
