import { Component, ViewChild, ElementRef, AfterViewInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements AfterViewInit {
  title = 'Precios Combustibles';
  headerHidden = false;
  private lastScrollTop = 0;

  @ViewChild('mainContent') mainContent!: ElementRef;

  ngAfterViewInit() {
    if (this.mainContent) {
      this.mainContent.nativeElement.addEventListener('scroll', (event: any) => {
        this.onScroll(event);
      });
    }
  }

  onScroll(event: any) {
    const scrollTop = event.target.scrollTop;
    const scrollingDown = scrollTop > this.lastScrollTop;

    if (scrollingDown && scrollTop > 50) {
      this.headerHidden = true;
    } else {
      this.headerHidden = false;
    }

    this.lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
  }
}
<div class="app-container">
  <header class="header" [class.hidden]="headerHidden">
    <div class="container">
      <h1>⛽ Precios de Combustibles</h1>
      <p class="subtitle">Datos actualizados del MITECO</p>
    </div>
  </header>

  <main class="container" #mainContent [class.header-hidden]="headerHidden">
    <app-lista-gasolineras></app-lista-gasolineras>
  </main>
</div>
