import { Component, OnInit, OnDestroy, ChangeDetectorRef } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { GasolinerasService } from '../../services/gasolineras.service';
import { GasolineraSimplificada, TipoCombustible } from '../../models/gasolinera.model';

@Component({
  selector: 'app-lista-gasolineras',
  templateUrl: './lista-gasolineras.component.html',
  styleUrls: ['./lista-gasolineras.component.scss']
})
export class ListaGasolinerasComponent implements OnInit, OnDestroy {
  gasolineras: GasolineraSimplificada[] = [];
  gasolinerasFiltradas: GasolineraSimplificada[] = [];
  provincias: string[] = [];
  localidades: string[] = [];
  loading = true;
  error: string | null = null;
  ubicacionUsuario: { lat: number; lng: number } | null = null;
  mostrandoCercanas = false;

  filtros = {
    provincia: '',
    localidad: '',
    codigoPostal: '',
    tipoCombustible: 'diesel' as keyof typeof this.nombresCombustibles
  };

  nombresCombustibles = {
    gasolina95: 'Gasolina 95',
    gasolina98: 'Gasolina 98',
    diesel: 'Di√©sel',
    dieselPremium: 'Di√©sel Premium',
    glp: 'GLP',
    gnc: 'GNC',
    bioetanol: 'Bioetanol',
    biodiesel: 'Biodi√©sel'
  };

  private destroy$ = new Subject<void>();

  constructor(private gasolinerasService: GasolinerasService, private cdr: ChangeDetectorRef) {}

  ngOnInit(): void {
    this.cargarGasolineras();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  cargarGasolineras(): void {
    this.loading = true;
    this.error = null;
    console.log('üîÑ Iniciando carga de gasolineras...');

    this.gasolinerasService.obtenerGasolineras()
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (gasolineras) => {
          console.log('‚úÖ Gasolineras cargadas:', gasolineras.length);
          console.log('üìç Primeras 5 gasolineras:', gasolineras.slice(0, 5));
          this.gasolineras = gasolineras;
          this.extraerProvinciasYLocalidades();
          this.aplicarFiltros();
          console.log('üéØ Gasolineras filtradas:', this.gasolinerasFiltradas.length);
          this.loading = false;
          console.log('üé® Loading puesto a false');
          this.cdr.detectChanges();
          console.log('üé® detectChanges ejecutado');
        },
        error: (err) => {
          console.error('‚ùå Error al cargar gasolineras:', err);
          this.error = 'Error al cargar los datos. Por favor, intenta de nuevo m√°s tarde.';
          this.loading = false;
        }
      });
  }

  extraerProvinciasYLocalidades(): void {
    const provinciasSet = new Set<string>();
    const localidadesSet = new Set<string>();

    this.gasolineras.forEach(g => {
      if (g.provincia) provinciasSet.add(g.provincia);
      if (g.localidad) localidadesSet.add(g.localidad);
    });

    this.provincias = Array.from(provinciasSet).sort();
    this.localidades = Array.from(localidadesSet).sort();
    console.log('üìå Provincias extra√≠das:', this.provincias.length);
    console.log('üèôÔ∏è Localidades extra√≠das:', this.localidades.length);
  }

  aplicarFiltros(): void {
    let resultado = [...this.gasolineras];
    console.log('üîç Aplicando filtros. Total inicial:', resultado.length);
    console.log('üîß Filtros actuales:', this.filtros);

    if (this.filtros.provincia) {
      resultado = resultado.filter(g => 
        g.provincia.toLowerCase().includes(this.filtros.provincia.toLowerCase())
      );
      console.log('üìç Despu√©s de provincia:', resultado.length);
    }

    if (this.filtros.localidad) {
      resultado = resultado.filter(g => 
        g.localidad.toLowerCase().includes(this.filtros.localidad.toLowerCase())
      );
      console.log('üèôÔ∏è Despu√©s de localidad:', resultado.length);
    }

    if (this.filtros.codigoPostal) {
      resultado = resultado.filter(g => 
        g.codigoPostal.includes(this.filtros.codigoPostal)
      );
      console.log('üìÆ Despu√©s de CP:', resultado.length);
    }

    // Ordenar por precio del combustible seleccionado (mostrar sin precio al final)
    resultado = resultado.sort((a, b) => {
      const precioA = a.precios[this.filtros.tipoCombustible] || Infinity;
      const precioB = b.precios[this.filtros.tipoCombustible] || Infinity;
      return precioA - precioB;
    });
    console.log('üí∞ Despu√©s de ordenar por precio:', resultado.length);

    this.gasolinerasFiltradas = resultado;
    console.log('‚ú® Gasolineras filtradas finales:', this.gasolinerasFiltradas.length);
    if (this.gasolinerasFiltradas.length > 0) {
      console.log('üèÜ Top 3 m√°s baratas:', this.gasolinerasFiltradas.slice(0, 3));
    }
  }

  onFiltrosChange(filtros: any): void {
    this.filtros = { ...this.filtros, ...filtros };
    this.mostrandoCercanas = false;
    this.aplicarFiltros();
  }

  buscarCercanas(): void {
    if (!navigator.geolocation) {
      this.error = 'Tu navegador no soporta geolocalizaci√≥n.';
      return;
    }

    this.loading = true;
    this.error = null;

    navigator.geolocation.getCurrentPosition(
      (position) => {
        this.ubicacionUsuario = {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        };
        this.mostrarGasolinerasCercanas();
        this.loading = false;
      },
      (error) => {
        this.loading = false;
        if (error.code === error.PERMISSION_DENIED) {
          this.error = 'Debes permitir el acceso a tu ubicaci√≥n para encontrar gasolineras cercanas.';
        } else {
          this.error = 'No se pudo obtener tu ubicaci√≥n. Por favor, intenta de nuevo.';
        }
        console.error('Error de geolocalizaci√≥n:', error);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  }

  mostrarGasolinerasCercanas(): void {
    if (!this.ubicacionUsuario) return;

    this.mostrandoCercanas = true;
    const gasolinerasConDistancia = this.gasolineras
      .map(g => ({
        ...g,
        distancia: this.calcularDistancia(
          this.ubicacionUsuario!.lat,
          this.ubicacionUsuario!.lng,
          g.latitud,
          g.longitud
        )
      }))
      .sort((a, b) => a.distancia - b.distancia);

    this.gasolinerasFiltradas = gasolinerasConDistancia as any;
    setTimeout(() => {
      this.cdr.detectChanges();
    }, 0);
  }

  calcularDistancia(lat1: number, lon1: number, lat2: number, lon2: number): number {
    // F√≥rmula de Haversine para calcular distancia entre dos puntos en la Tierra
    const R = 6371; // Radio de la Tierra en kil√≥metros
    const dLat = this.toRadians(lat2 - lat1);
    const dLon = this.toRadians(lon2 - lon1);

    const a = 
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(this.toRadians(lat1)) * Math.cos(this.toRadians(lat2)) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);

    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distancia = R * c; // Distancia en kil√≥metros

    return distancia;
  }

  toRadians(grados: number): number {
    return grados * (Math.PI / 180);
  }

  refrescar(): void {
    this.gasolinerasService.refrescar();
    this.cargarGasolineras();
  }

  trackByFn(index: number, item: GasolineraSimplificada): string {
    return item.id;
  }
}
<div class="lista-container">
  <!-- PANEL DE DEBUG FIJO ARRIBA SIN CONDICIONES -->
  <div class="debug-simple">
    <p>üî¥ Estado: loading={{ loading }}, error={{ error }}, gasolinerasFiltradas.length={{ gasolinerasFiltradas.length }}</p>
  </div>

  <div class="filtros-sticky" *ngIf="!loading">
    <app-filtros 
      (filtrosChange)="onFiltrosChange($event)"
      (buscarCercanas)="buscarCercanas()"
      [provincias]="provincias"
      [localidades]="localidades"
      [todasLasGasolineras]="gasolineras">
    </app-filtros>

    <div class="header-lista">
      <h2>Gasolineras disponibles</h2>
      <button class="btn-primary" (click)="refrescar()" [disabled]="loading">
        üîÑ {{ loading ? 'Cargando...' : 'Actualizar' }}
      </button>
    </div>
  </div>

  <div class="contenido-scrollable">
    <div *ngIf="loading" class="loading">
      <div class="spinner"></div>
      <p>Cargando datos del MITECO...</p>
    </div>

    <div *ngIf="error" class="error">
      {{ error }}
    </div>

    <!-- Debug: Mostrar estado actual -->
    <div class="debug-info">
      <p>üîç Debug - gasolinerasFiltradas.length: {{ gasolinerasFiltradas.length }}</p>
      <p>üîç Debug - gasolineras.length: {{ gasolineras.length }}</p>
      <p>üîç Debug - loading: {{ loading }}</p>
      <p>üîç Debug - error: {{ error }}</p>
    </div>

    <!-- QUITANDO COMPLETAMENTE EL *ngIf PARA PROBAR -->
    <div class="resultados">
      <p style="background: lime; padding: 20px; font-size: 24px;">‚úÖ ESTO DEBER√çA SER VISIBLE SIEMPRE</p>

      <div class="test-gasolinera">
        <h4>üß™ TEST - Primera gasolinera:</h4>
        <p><strong>Nombre:</strong> {{ gasolinerasFiltradas[0]?.nombre }}</p>
        <p><strong>Direcci√≥n:</strong> {{ gasolinerasFiltradas[0]?.direccion }}</p>
        <p><strong>Localidad:</strong> {{ gasolinerasFiltradas[0]?.localidad }}</p>
        <p><strong>Precio diesel:</strong> {{ gasolinerasFiltradas[0]?.precios?.diesel }}</p>
      </div>

      <div class="grid-gasolineras">
        <!-- Prueba: Mostrar solo la primera tarjeta SIN ngFor -->
        <div style="border: 3px solid red; padding: 20px;">
          <p>üß™ PRUEBA DE TARJETA - Primera gasolinera:</p>
          <app-tarjeta-gasolinera 
            *ngIf="gasolinerasFiltradas[0]"
            [gasolinera]="gasolinerasFiltradas[0]"
            [combustibleDestacado]="filtros.tipoCombustible">
          </app-tarjeta-gasolinera>
        </div>
      </div>
    </div>

    <div *ngIf="!loading && !error && (!gasolinerasFiltradas || gasolinerasFiltradas.length === 0)" class="sin-resultados">
      No se encontraron gasolineras con los filtros aplicados.
    </div>
  </div>
</div>
