import { Component, EventEmitter, Output, Input, OnChanges, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-filtros',
  templateUrl: './filtros.component.html',
  styleUrls: ['./filtros.component.scss']
})
export class FiltrosComponent implements OnChanges {
  @Output() filtrosChange = new EventEmitter<any>();
  @Input() provincias: string[] = [];
  @Input() localidades: string[] = [];
  @Input() todasLasGasolineras: any[] = [];

  localidadesFiltradas: string[] = [];

  filtros = {
    provincia: '',
    localidad: '',
    codigoPostal: '',
    tipoCombustible: 'diesel'
  };

  tiposCombustible = [
    { value: 'gasolina95', label: 'Gasolina 95' },
    { value: 'gasolina98', label: 'Gasolina 98' },
    { value: 'diesel', label: 'Di√©sel' },
    { value: 'dieselPremium', label: 'Di√©sel Premium' },
    { value: 'glp', label: 'GLP' },
    { value: 'gnc', label: 'GNC' },
    { value: 'bioetanol', label: 'Bioetanol' },
    { value: 'biodiesel', label: 'Biodi√©sel' }
  ];

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['localidades'] && this.localidades) {
      this.localidadesFiltradas = [...this.localidades];
    }
  }

  onProvinciaChange(): void {
    // Resetear localidad cuando cambia la provincia
    this.filtros.localidad = '';

    // Filtrar localidades de la provincia seleccionada
    if (this.filtros.provincia && this.todasLasGasolineras.length > 0) {
      const localidadesSet = new Set<string>();
      this.todasLasGasolineras
        .filter(g => g.provincia === this.filtros.provincia)
        .forEach(g => {
          if (g.localidad) localidadesSet.add(g.localidad);
        });
      this.localidadesFiltradas = Array.from(localidadesSet).sort();
    } else {
      this.localidadesFiltradas = [...this.localidades];
    }

    this.onFiltroChange();
  }

  onFiltroChange(): void {
    this.filtrosChange.emit(this.filtros);
  }

  limpiarFiltros(): void {
    this.filtros = {
      provincia: '',
      localidad: '',
      codigoPostal: '',
      tipoCombustible: 'diesel'
    };
    this.localidadesFiltradas = [...this.localidades];
    this.filtrosChange.emit(this.filtros);
  }
}
<div class="filtros-container">
  <h3>üîç Filtrar gasolineras</h3>

  <div class="filtros-grid">
    <div class="filtro-item">
      <label for="tipoCombustible">Tipo de combustible:</label>
      <select 
        id="tipoCombustible" 
        [(ngModel)]="filtros.tipoCombustible" 
        (change)="onFiltroChange()">
        <option *ngFor="let tipo of tiposCombustible" [value]="tipo.value">
          {{ tipo.label }}
        </option>
      </select>
    </div>

    <div class="filtro-item">
      <label for="provincia">Provincia:</label>
      <select 
        id="provincia" 
        [(ngModel)]="filtros.provincia" 
        (change)="onProvinciaChange()">
        <option value="">Todas las provincias</option>
        <option *ngFor="let provincia of provincias" [value]="provincia">
          {{ provincia }}
        </option>
      </select>
    </div>

    <div class="filtro-item">
      <label for="localidad">Localidad:</label>
      <select 
        id="localidad" 
        [(ngModel)]="filtros.localidad" 
        (change)="onFiltroChange()"
        [disabled]="!filtros.provincia">
        <option value="">Todas las localidades</option>
        <option *ngFor="let localidad of localidadesFiltradas" [value]="localidad">
          {{ localidad }}
        </option>
      </select>
    </div>

    <div class="filtro-item">
      <label for="codigoPostal">C√≥digo Postal:</label>
      <input 
        type="text" 
        id="codigoPostal" 
        [(ngModel)]="filtros.codigoPostal" 
        (input)="onFiltroChange()"
        placeholder="Ej: 28001">
    </div>

    <div class="filtro-item btn-container">
      <button class="btn-limpiar" (click)="limpiarFiltros()">
        üóëÔ∏è Limpiar filtros
      </button>
    </div>
  </div>
</div>
